import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
import DisplayTask from "./components/DisplayTask"

class App extends React.Component {

  state = {
    categoryDisplay: "",
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ]
  }


  handleClick =(event) => {
    event.target.innerText === "All" ? 
    this.setState({categoryDisplay: ""}) :
    this.setState({categoryDisplay: event.target.innerText})
  }

  handleDelete  = (deleteTask) => {
    this.setState({
      tasks: tasks.filter(task => task !== deleteTask)
    })
  }

  render() {
    let filterTasks = this.state.tasks.filter(task => task.category.includes(this.state.categoryDisplay))
    return (
      <div className="App">
        <h2>My tasks</h2>
        <div className='categories'>
          <h5>Category filters</h5>
          {CATEGORIES.map(category => <button onClick={this.handleClick}>{category}</button>)}
        </div>
        <div className="tasks">
          <h5>Tasks</h5>
          {filterTasks.map((task,i) => <DisplayTask key={i} task={task} handleDelete={this.handleDelete}/>)}
        </div>
      </div>
    );
  }
}


export default App;
